mapper.select:
SELECT *
FROM tb_non_product_manage



		AND equipment = #{equipment_name}



		AND action_date >=
		#{startDate}


		AND action_date <=
		#{endDate}


ORDER BY action_date DESC

SELECT *
FROM tb_tustest


		AND mch_name = #{mch_name}


		AND t_year = #{t_year}


ORDER BY id DESC

SELECT * FROM tb_incoming_test
AND page = #{page}
AND in_day LIKE CONCAT(#{in_day}, '%')

mapper.update:
UPDATE tb_incoming_test
in_day = #{in_day}, page = #{page}, supplier = #{supplier}, medicine = #{medicine}, lot = #{lot}, nv_1 = #{nv_1}, nv_2 = #{nv_2}, color_1 = #{color_1}, color_2 = #{color_2}, sensuality_1_1 = #{sensuality_1_1}, sensuality_2_1 = #{sensuality_2_1}, sensuality_1_2 = #{sensuality_1_2}, sensuality_2_2 = #{sensuality_2_2}, confirm = #{confirm}, file_name = #{file_name}, writer = #{writer}, confirmer = #{confirmer}, ph_1 = #{ph_1}, ph_2 = #{ph_2}, specific_gravity_1 = #{specific_gravity_1}, specific_gravity_2 = #{specific_gravity_2}, viscosity_1 = #{viscosity_1}, viscosity_2 = #{viscosity_2}, hardness_1 = #{hardness_1}, hardness_2 = #{hardness_2}, short_1 = #{short_1}, short_2 = #{short_2}, appearance_1 = #{appearance_1}, appearance_2 = #{appearance_2}, ex_1_1 = #{ex_1_1}, ex_1_2 = #{ex_1_2}, ex_2_1 = #{ex_2_1}, ex_2_2 = #{ex_2_2}, result = #{result}, update_id = #{update_id},
WHERE no = #{no}

UPDATE tb_medicine_standard
naoh_result = #{naoh_result}, hcl = #{hcl}, liter = #{liter}, naoh = #{naoh}, sc300a = #{sc300a}, sc300b = #{sc300b}, condense = #{condense}, after_naoh = #{after_naoh},
WHERE num = #{num}

UPDATE tb_test_manage
sst1 = #{sst1}, sst2 = #{sst2}, sst3 = #{sst3}, cct1 = #{cct1}, cct2 = #{cct2}, cct3 = #{cct3}, contact1 = #{contact1}, contact2 = #{contact2}, contact3 = #{contact3}, gattach1 = #{gattach1}, gattach2 = #{gattach2}, gattach3 = #{gattach3}, after_attach1 = #{after_attach1}, after_attach2 = #{after_attach2}, after_attach3 = #{after_attach3}, heat1 = #{heat1}, heat2 = #{heat2}, heat3 = #{heat3}, clean1 = #{clean1}, clean2 = #{clean2}, clean3 = #{clean3}, shot1 = #{shot1}, shot2 = #{shot2}, shot3 = #{shot3}, etc1 = #{etc1}, etc2 = #{etc2}, etc3 = #{etc3}, etc4 = #{etc4}, etc5 = #{etc5}, etc6 = #{etc6},
WHERE number = #{number}

mapper.insert:
INSERT INTO
tb_clean12 (
test_num, date, mch_name, acid_reduce,
naoh_density, min_spec, max_spec, file_name
)
VALUES (
#{test_num}, #{date}, #{mch_name}, #{acid_reduce},
#{naoh_density}, #{min_spec}, #{max_spec}, #{file_name}
)

mapper.select:
SELECT c.id, c.test_num, c.date, c.mch_name, c.acid_reduce, c.naoh_density, c.min_spec, c.max_spec, c.file_name,
/* 조인 실패시 '범위값초과', 조인 성공시 스펙 범위 판정 */ CASE WHEN m.num IS NULL THEN '범위값초과' WHEN CAST(NULLIF(TRIM(REPLACE(REPLACE(c.naoh_density, ',', ''), '%','')), '') AS DECIMAL(10,2)) BETWEEN CAST(NULLIF(TRIM(REPLACE(REPLACE(c.min_spec, ',', ''), '%','')), '') AS DECIMAL(10,2)) AND CAST(NULLIF(TRIM(REPLACE(REPLACE(c.max_spec, ',', ''), '%','')), '') AS DECIMAL(10,2)) THEN '합격' ELSE '불합격' END AS result,
/* m 테이블 값은 조인 실패 시 0으로 대체 */ IFNULL(m.num, 0) AS standard_num, IFNULL(m.naoh_result, 0) AS naoh_result, IFNULL(m.hcl, 0) AS hcl, IFNULL(m.liter, 0) AS liter, IFNULL(m.naoh, 0) AS naoh, IFNULL(m.sc300a, 0) AS sc300a, IFNULL(m.sc300b, 0) AS sc300b, IFNULL(m.condense, 0) AS condense, IFNULL(m.after_naoh, 0) AS after_naoh, IFNULL(m.table_code, 0) AS table_code
FROM tb_clean12 c LEFT JOIN tb_medicine_standard m ON m.naoh_result = ( FLOOR( CAST(NULLIF(TRIM(REPLACE(REPLACE(c.naoh_density, ',', ''), '%','')), '') AS DECIMAL(10,2)) / 2 ) * 2 ) AND ( (c.mch_name = '1호기' AND m.table_code = 1) OR (c.mch_name = '2호기' AND m.table_code = 2) )
WHERE 1 = 1
AND c.date = #{date}
