mapper.delete:
DELETE FROM tb_new_inventorystatus
WHERE id IN

    #{id}

mapper.select:
SELECT * FROM v_inventory_balance WHERE `reg_date` LIKE CONCAT(#{startDate}, '%') AND `lot_no` IS NOT NULL
AND `medicine_name` = #{medicine_name}
ORDER BY STR_TO_DATE(`reg_date`, '%Y-%m-%d %H:%i:%s') ASC

SELECT ROW_NUMBER() OVER(ORDER BY bb.end_time) AS r_num, IFNULL(bb.division_num,'2') AS a, -- üîπ ez_geo_hst.division_num Í∞ÄÏ†∏Ïò§Í∏∞ bb.group_id, bb.start_time, bb.end_time, bb.tong_day, bb.weight_day, bb.item_nm, bb.item_cd, bb.next_facility, bb.wrk_ord_no, CASE WHEN bb.division_weight = 0 THEN 0 ELSE CASE WHEN (bb.weight_day / bb.division_weight) > 1 THEN FLOOR((bb.weight_day / bb.division_weight)) + 1 ELSE 1 END END AS a2, '' AS b, '' AS c, '' AS d, CASE WHEN bb.wrk_ord_no LIKE '%WR%' THEN 'Ïû¨ÏûëÏóÖ' ELSE 'Ïã†Í∑ú' END AS e, '' AS f, hst2_barcode, barcode_no FROM ( SELECT aa.mach_code, aa.end_time, aa.start_time, aa.tong_day, aa.weight_day, aa.item_cd, aa.item_nm, aa.wrk_ord_no, ab.coating_nm AS next_facility, ab.group_id, COALESCE(aa.std_weight, 0) AS division_weight, eh.division_num, -- üîπ ez_geo_hst.division_num JOIN hst2_barcode, barcode_no FROM ( SELECT lows.mach_code, lows.barcode_no, ROUND(lows.weight, 0) AS weight_day, 1 AS tong_day, fac.facility_name, fac.std_weight, lows.start_time, lows.end_time, lows.item_cd, lows.item_nm, lows.wrk_ord_no, LEAD(lows.end_time) OVER( PARTITION BY lows.mach_code, lows.barcode_no ORDER BY lows.end_time DESC ) AS next_start_time FROM tb_lowdata_save AS lows INNER JOIN tb_facility AS fac ON lows.mach_code = fac.facility_mach_code WHERE 1=1
AND lows.end_time >= #{s_time}
AND lows.end_time < #{e_time}
AND lows.mach_code IN (${m_code2})
AND lows.weight IS NOT NULL ) AS aa LEFT JOIN pwo_geo_division_data AS ab ON aa.item_cd = ab.item_cd LEFT JOIN ( SELECT barcode_no AS hst2_barcode, MAX(division_num) AS division_num FROM ez_geo_hst2 WHERE mach_code = #{m_code} AND wrk_gbn_state = 'IN' GROUP BY barcode_no ) AS eh ON aa.barcode_no = eh.hst2_barcode -- üîπ division_num Í∞ÄÏ†∏Ïò§Îäî ÏúÑÏπòÎ•º Ïù¥Ï™ΩÏúºÎ°ú ÏòÆÍπÄ ) AS bb WHERE 1=1 AND bb.mach_code = #{m_code} ORDER BY bb.end_time;

SELECT ROW_NUMBER() OVER(ORDER BY bb.end_time) AS r_num, IFNULL(bb.division_num,'0') AS a, -- üîπ ez_geo_hst.division_num Í∞ÄÏ†∏Ïò§Í∏∞ bb.group_id, bb.start_time, bb.end_time, bb.tong_day, bb.weight_day, bb.item_nm, bb.item_cd, bb.next_facility, bb.wrk_ord_no, CASE WHEN bb.division_weight = 0 THEN 0 ELSE CASE WHEN (bb.weight_day / bb.division_weight) > 1 THEN FLOOR((bb.weight_day / bb.division_weight)) + 1 ELSE 1 END END AS a2, '' AS b, '' AS c, '' AS d, CASE WHEN bb.wrk_ord_no LIKE '%WR%' THEN 'Ïû¨ÏûëÏóÖ' ELSE 'Ïã†Í∑ú' END AS e, '' AS f, hst2_barcode, barcode_no FROM ( SELECT aa.mach_code, aa.end_time, aa.start_time, aa.tong_day, aa.weight_day, aa.item_cd, aa.item_nm, aa.wrk_ord_no, ab.coating_nm AS next_facility, ab.group_id, COALESCE(aa.std_weight, 0) AS division_weight, eh.division_num, -- üîπ ez_geo_hst.division_num JOIN hst2_barcode, barcode_no FROM ( SELECT lows.mach_code, lows.barcode_no, ROUND(lows.weight, 0) AS weight_day, 1 AS tong_day, fac.facility_name, fac.std_weight, lows.start_time, lows.end_time, lows.item_cd, lows.item_nm, lows.wrk_ord_no, LEAD(lows.end_time) OVER( PARTITION BY lows.mach_code, lows.barcode_no ORDER BY lows.end_time DESC ) AS next_start_time FROM tb_lowdata_save_list_condition AS lows INNER JOIN tb_facility AS fac ON lows.mach_code = fac.facility_mach_code WHERE 1=1
AND lows.start_time >= #{s_time}
AND lows.start_time < #{e_time}
AND lows.mach_code IN (${m_code2})
AND lows.weight IS NOT NULL ) AS aa LEFT JOIN pwo_geo_division_data AS ab ON aa.item_cd = ab.item_cd LEFT JOIN ( SELECT barcode_no AS hst2_barcode, MAX(division_num) AS division_num FROM ez_geo_hst2_save3 WHERE mach_code = #{m_code} AND wrk_gbn_state = 'IN' GROUP BY barcode_no ) AS eh ON aa.barcode_no = eh.hst2_barcode -- üîπ division_num Í∞ÄÏ†∏Ïò§Îäî ÏúÑÏπòÎ•º Ïù¥Ï™ΩÏúºÎ°ú ÏòÆÍπÄ ) AS bb WHERE 1=1 AND bb.mach_code = #{m_code} ORDER BY bb.start_time;

SELECT barcode_no, MAX(item_cd) AS item_cd, MAX(item_nm) AS item_nm, GROUP_CONCAT(IF(mach_code LIKE 'W%', CONCAT(facility_name,' - ',start_time), NULL)) AS 'w_s', GROUP_CONCAT(IF(mach_code LIKE 'W%', CONCAT(facility_name,' - ',end_time), NULL)) AS 'w_e', GROUP_CONCAT(IF(mach_code LIKE 'S%', CONCAT(facility_name,' - ',start_time), NULL)) AS 's_s', GROUP_CONCAT(IF(mach_code LIKE 'S%', CONCAT(facility_name,' - ',end_time), NULL)) AS 's_e', GROUP_CONCAT(IF(mach_code LIKE 'G03-%', CONCAT(facility_name,' - ',start_time), NULL)) AS 'g_s', GROUP_CONCAT(IF(mach_code LIKE 'G03-%', CONCAT(facility_name,' - ',end_time), NULL)) AS 'g_e', GROUP_CONCAT(IF(mach_code LIKE 'G04-%', CONCAT(facility_name,' - ',start_time), NULL)) AS 'mlpl_s', GROUP_CONCAT(IF(mach_code LIKE 'G04-%', CONCAT(facility_name,' - ',end_time), NULL)) AS 'mlpl_e' FROM ( SELECT A.barcode_no, DATE_FORMAT(A.start_time, '%Y-%m-%d %H:%i:%s') AS start_time, DATE_FORMAT(A.end_time, '%Y-%m-%d %H:%i:%s') AS end_time, A.item_cd, A.item_nm, A.mach_code, B.facility_name FROM tb_lowdata_save A LEFT OUTER JOIN tb_facility B ON A.mach_code = B.facility_mach_code WHERE 1=1 AND start_time IS NOT NULL AND end_time IS NOT NULL
AND A.end_time >= #{startDate}
AND A.end_time <= #{endDate}
AND A.barcode_no = #{barcode_no}
) AS AA GROUP BY barcode_no ORDER BY MAX(end_time) DESC;
